{% set status = ['HomeTeam_1','AwayTeam_1'] %}

with summary as (
{% for club in football_clubs %}
    {% for st in status %}
    select 
        {{ st }} as team,
        {% if st == 'HomeTeam' %}
                case 
                    when FTR_1 = 'H' then 3
                    when FTR_1 = 'D' then 1
                    else 0 end points
        {% else %}
                case 
                    when FTR_1 = 'A' then 3
                    when FTR_1 = 'D' then 1
                    else 0 end points
        {% endif %}
    from {{ table_name }}
    where {{ st }} = '{{ club }}'
        {% if not loop.last %} UNION ALL {% endif %}
    {% endfor %}
    {% if not loop.last %} UNION ALL {% endif %}
{% endfor %}
)


select 
    team, 
    sum(points) as total_points
from summary
group by team
order by total_points desc
